mapper.select:
select F_FINDCOMPNAME(company_code)AS comp_name, o.order_no, p.product_code, p.product_name, p.std_id, p.unit_id, o.in_date, o.out_date, o.order_count, o.export_count, (o.order_count - o.export_count) as dCount, CASE WHEN o.plan_count = 0 THEN '미계획' WHEN o.order_count > o.plan_count THEN '진행중' ELSE '완료' END AS order_state FROM orders o, detailexport e, product p WHERE o.in_date BETWEEN TO_DATE(#{fromDate},'YYYY-MM-DD') AND TO_DATE(#{toDate}, 'YYYY-MM-DD') AND o.product_code=p.product_code AND o.order_no=e.order_no
and o.company_code=#{companyCode}
and o.product_code=#{productCode}
and o.plan_count = 0
and o.order_count > o.plan_count AND o.plan_count > 0
and o.order_count = o.plan_count
