mapper.select:
select F_FINDCOMPNAME(company_code)AS comp_name, TO_CHAR(o.in_date,'YYYY-MM-DD')as in_date, TO_CHAR(o.out_date,'YYYY-MM-DD')as out_date, o.order_no, p.product_code,p.product_name, p.std_id,p.unit_id,o.order_count,o.out_count, (o.order_count - o.out_count) as not_count, CASE WHEN o.plan_count = 0 THEN '미계획' WHEN o.order_count > o.plan_count THEN '진행중' ELSE '완료' END AS order_state FROM orders o, product p WHERE in_date BETWEEN TO_DATE(#{fromDate},'YYYY-MM-DD') AND TO_DATE(#{toDate}, 'YYYY-MM-DD') AND o.product_code=p.product_code
and o.company_code=#{companyCode}
and o.product_code=#{productCode}
and o.plan_count = 0
and o.order_count > o.plan_count AND o.plan_count > 0
and o.order_count = o.plan_count

select distinct p.product_code,p.product_name,p.std_id,p.unit_id,o.order_no,o.order_count, o.out_count,(o.order_count - o.out_count) as un_export, (SELECT SUM(product_count) FROM storage WHERE product_code = o.product_code AND product_state='포장완료') as dayCount from orders o, storage s, product p,export e where o.product_code = p.product_code and o.product_code = s.product_code and e.export_date=#{exportDate}
and e.export_code=#{exportCode}
and o.company_code=#{companyCode}
