mapper.select:
SELECT count(*) 
FROM material 
WHERE material_code = #{materialCode}

mapper.update:
UPDATE material 
SET material_name = #{materialName}, unit_no = #{unitNo}, company_code =  #{companyCode}
, MIN_STOCK =  #{minStock} , MAX_STOCK =  #{maxStock}, SAVE_STOCK =  #{saveStock}, MONTH_STOCK =  #{monthStock}
WHERE material_code = #{materialCode}

mapper.select:
SELECT inorder_code, inorder_date, material_code, f_findMatName(material_code) as material_name, f_findCompName(company_code) as company_name, (select max(io_date) from inout where inorder_code = inorder.inorder_code) as in_date, inorder_count, nvl((select sum(io_volume) from inout where inorder_code = inorder.inorder_code),0) as in_count, inorder_count - nvl((select sum(io_volume) from inout where inorder_code = inorder.inorder_code),0) as yet_count, comments
FROM inorder
trunc(io_date) BETWEEN #{startDate} AND #{endDate}
AND company_code = #{companyCode}
AND material_code = #{materialCode}
ORDER BY ${sort}
desc

SELECT io_code, io_date, material_code, F_FINDMATNAME(material_code) as material_name, (SELECT unit_no FROM material WHERE material_code = inout.material_code) as unit_no, inorder_code, io_volume, unit_price, io_volume * unit_price as price, lot_no, (SELECT stock FROM stock WHERE material_code = inout.material_code) as stock, (SELECT f_findCompName(company_code) FROM inorder WHERE inorder_code = inout.inorder_code) as company_name, (SELECT company_code FROM inorder WHERE inorder_code = inout.inorder_code) as company_code, process_code, (SELECT process_name FROM process WHERE process_code = inout.process_code) as process_name FROM inout
inout_no = #{ioType} AND trunc(io_date) BETWEEN #{startDate} AND #{endDate}
AND (SELECT company_code FROM inorder WHERE inorder_code = inout.inorder_code) = #{companyCode}
AND material_code = #{materialCode}
AND process_code = #{processCode}
ORDER BY ${sort}
desc

mapper.update:
UPDATE inout 
SET io_date = #{ioDate}, inorder_code = #{inorderCode}, material_code = #{materialCode}, io_volume = #{ioVolume}, unit_price = #{unitPrice}
			,lot_no = #{lotNo}, process_code = #{processCode}
WHERE io_code = #{ioCode}
