mapper.select:
SELECT count(*) 
FROM material 
WHERE material_code = #{materialCode}

SELECT inorder_code, inorder_date, material_code, f_findMatName(material_code) as material_name, company_code, f_findCompName(company_code) as company_name, (select max(io_date) from inout where inorder_code = inorder.inorder_code) as in_date, inorder_count, nvl((select sum(io_volume) from inout where inorder_code = inorder.inorder_code),0) as in_count, inorder_count - nvl((select sum(io_volume) from inout where inorder_code = inorder.inorder_code),0) as yet_count, comments
FROM inorder
trunc(inorder_date) BETWEEN #{startDate} AND #{endDate}
AND company_code = #{companyCode}
AND material_code = #{materialCode}
ORDER BY ${sort}
desc

mapper.update:
UPDATE inorder
SET inorder_date = #{inorderDate}, inorder_count = #{inorderCount}, company_code = #{companyCode}, material_code = #{materialCode}, comments = #{comments}
WHERE inorder_code = #{inorderCode}

mapper.select:
SELECT io_code, io_date, material_code, F_FINDMATNAME(material_code) as material_name, (SELECT unit_no FROM material WHERE material_code = inout.material_code) as unit_no, inorder_code, io_volume, unit_price, io_volume * unit_price as price, lot_no, F_FIND_MAT_STOCK(inout.material_code) as stock, (SELECT f_findCompName(company_code) FROM inorder WHERE inorder_code = inout.inorder_code) as company_name, (SELECT company_code FROM inorder WHERE inorder_code = inout.inorder_code) as company_code, process_code, (SELECT process_name FROM process WHERE process_code = inout.process_code) as process_name, comments FROM inout
trunc(io_date) BETWEEN #{startDate} AND #{endDate}
AND inout_no = #{inoutNo}
AND (SELECT company_code FROM inorder WHERE inorder_code = inout.inorder_code) = #{companyCode}
AND material_code = #{materialCode}
AND mat_no = #{matNo}
AND process_code = #{processCode}
ORDER BY ${sort}
desc

SELECT io_code, lot_no, io_date, io_volume, material_code, (SELECT unit_no FROM material WHERE material_code = inout.material_code) as unit_no, f_find_mat_name(material_code) as material_name, (select distinct unit_price from inout i where lot_no = inout.lot_no and inout_no = '01' ) as unit_price, inout_no, comments, F_MAT_COUNT(lot_no) AS lot_stock, (select distinct unit_price from inout i where lot_no = inout.lot_no and inout_no = '01' ) * io_volume as stock FROM inout WHERE inout_no NOT IN ('01','02','05') AND TRUNC(io_date) BETWEEN #{startDate} AND #{endDate}
AND inout_no = #{inoutNo}
AND material_code = #{materialCode}

SELECT inout_no
FROM inout
WHERE io_code = #{ioCode}

mapper.insert:
UPDATE inout
SET io_volume = #{ioVolume}, inout_no = #{inoutNo}, comments = #{comments}
WHERE io_code = #{ioCode}

mapper.update:
UPDATE COMTCCMMNCODE
SET code_id_nm = #{codeIdNm}, code_id_dc = #{codeIdDc}, use_at = #{codeIdUseAt}
WHERE code_id = #{codeId}

UPDATE COMTCCMMNDETAILCODE
SET code_nm = #{codeNm}, code_dc = #{codeDc}, use_at=#{codeUseAt}
WHERE code_id = #{codeId} and code = #{code}
