mapper.update:
UPDATE detailplan SET work_date = #{workDate}, work_count = #{workCount},
comments = #{comments} 
WHERE product_lot = #{productLot}

UPDATE inputMat SET
input_count = #{inputCount}, comments = #{comments} 
WHERE input_idx = #{inputIdx}

mapper.select:
SELECT DISTINCT w.work_no, w.process_code, TO_CHAR(w.work_date,'YYYY-MM-DD') work_date, w.product_code, w.work_count, w.error_count FROM work w, process p WHERE w.work_date = #{workDate} AND w.process_code = p.process_code
AND p.process_name = #{workDiv}
ORDER BY w.work_date

mapper.insert:
INSERT INTO work VALUES(
(SELECT NVL(MAX(work_no)+1,0) FROM work),
#{processCode}, #{productLot}, #{workDate}, #{startTime}, #{endTime},
#{empCode}, #{errorCode}, #{productCode}, #{workCount}, #{errorCount}
)
