mapper.insert:
INSERT INTO plan VALUES(
(SELECT NVL(MAX(plan_code),0) FROM plan) + 1, #{planName}, #{planDate}, #{comments} 
)

INSERT INTO detailplan VALUES(
(SELECT NVL(MAX(productLot),0) FROM detailplan) + 1,


		#{planCode},


		(SELECT NVL(MAX(plan_code),0) FROM plan) + 1,


#{orderNo}, #{productCode}, #{workDate}, #{workCount}, #{comments} 
)

mapper.update:
UPDATE detailplan SET work_date = #{workDate}, work_count = #{workCount},
comments = #{comments} 
WHERE product_lot = #{productLot}

mapper.insert:
INSERT INTO inputMat VALUES(
(SELECT NVL(MAX(inputIdx),0) FROM inputMat) + 1,
#{productLot}, #{materialLot}, #{inputCount}, #{comments} 
)

mapper.update:
UPDATE inputMat SET lot_no = #{productLot}, material_lot = #{materialLot},
input_count = #{inputCount}, comments = #{comments} 
WHERE input_idx = #{inputIdx}
