mapper.insert:

        	SELECT NVL(MAX(plan_code),0)+1 FROM plan    

		INSERT INTO plan VALUES(
		#{planCode}, #{planName}, #{planDate}, #{comments} 
		)


        	SELECT NVL(MAX(product_lot),0)+1 FROM detailplan

		INSERT INTO detailplan VALUES(
		#{productLot}, #{planCode},	#{orderNo}, #{productCode}, #{workDate}, #{comments}, #{workCount}
		)

mapper.update:
UPDATE detailplan SET work_date = #{workDate}, work_count = #{workCount},
comments = #{comments} 
WHERE product_lot = #{productLot}

mapper.insert:

        	SELECT NVL(MAX(input_idx),0)+1 FROM inputmat

		INSERT INTO inputMat VALUES(
		#{inputIdx}, #{planCode}, #{orderNo}, #{materialLot}, #{inputCount}, #{comments} 
		)

mapper.update:
UPDATE inputMat SET lot_no = #{productLot}, material_lot = #{materialLot},
input_count = #{inputCount}, comments = #{comments} 
WHERE input_idx = #{inputIdx}

select.if:
SELECT i.*, m.material_code, m.material_name, F_MAT_COUNT(i.lot_no)
FROM inputmat i, material m
WHERE i.product_lot = #{productLot}
